var $searchQuery=$("#query").val(),searchAlbums=function(a){$.ajax({url:"https://api.spotify.com/v1/search",data:{q:a,type:"album"}}).done(function(){alert("Success")}).fail(function(){alert("Failed")}).always(function(){alert("Complete")})};$("form").submit(function(a){a.preventDefault(),searchAlbums($searchQuery)});