function populateresults(a){var s=a.albums.items;$.each(s,function(a,s){var u={id:s.id,name:s.name,thumb:s.images[1].url};$.ajax({url:"https://api.spotify.com/v1/albums/"+s.id,success:function(a){return u.release_date=a.release_date,"string"}}),albums.push(u)}),console.log("albums:"),console.log(albums)}function searchAlbums(){var a=input.val();$.ajax({url:"https://api.spotify.com/v1/search",data:{q:a,type:"album"},success:populateresults})}var input=$("#query"),albums=[];$(input).keyup(function(){searchAlbums()});