function searchAlbums(){var a=$input.val();$.ajax({url:"https://api.spotify.com/v1/search",data:{q:a,type:"album"},success:function(a){var e=$("#image-gallery"),i=a.albums.items,l="";$.each(i,function(a,e){var i=e.name,t=e.images[0].url,u=e.images[1].url;l+='<li class="gallery-item">',l+='<a href="'+t+'">',l+='<img src="'+u+'" alt="'+i+'">',l+="</a>",l+="</li>"}),e.html(l)}})}var $input=$("#query");$($input).keyup(function(){searchAlbums()}),$("a").click(function(a){a.preventDefault()});