function searchAlbums(){var a=$("#query").val();$.ajax({url:"https://api.spotify.com/v1/search",data:{q:a,type:"album"},success:function(a){var e=$("#image-gallery"),l=a.albums.items,s="";$.each(l,function(a,e){var l=e.name,u=e.images[0].url,i=e.images[1].url;s+='<li class="gallery-item">',s+='<a href="'+u+'">',s+='<img src="'+i+'" alt="'+l+'">',s+="</a>",s+="</li>"}),e.html(s)}})}var $input=$("#query");$($input).keyup(function(){searchAlbums()});