function fetchTracks(a,s){$.ajax({url:"https://api.spotify.com/v1/albums/"+a,success:function(a){s(a)}})}function displayResults(a){var s;$.each(array,function(a,l){s+='<li class="gallery-item">',s+='<a href="">',s+='<img src="" alt=">',s+="</a>",s+="</li>"}),$gallery.html(s)}function searchAlbums(a){$.ajax({url:"https://api.spotify.com/v1/search",data:{q:a,type:"album"},success:function(a){var s;$.each(array,function(a,l){s+='<li class="gallery-item">',s+='<a href="">',s+='<img src="" alt=">',s+="</a>",s+="</li>"}),$gallery.html(s)}})}var $searchQuery=$("#query").val();$("form").submit(function(a){a.preventDefault(),searchAlbums($searchQuery)});